	$( "#check-detail" ).bind( "tap", function( e ){
		if( $(this).hasClass('expanded')==false ){
			$(this).html('收起').addClass('expanded');
			$('#rules-full').slideDown(300);
			
		}else{
			$(this).html('查看详细').removeClass('expanded');
			$('#rules-full').slideUp(300);
		}
	  return false;
	});


