	//场外观众投票率
	$('#vote-list2').waypoint(function(direction)
		{
			if(direction=='down'){
				$('#vote-list2 li').eq(0).height('30%');
				$('#vote-list2 li').eq(1).height('60%');
				$('#vote-list2 li').eq(2).height('80%');
				$('#vote-list2 li').eq(3).height('20%');
				$('#vote-list2 li').eq(4).height('40%');
			}
		},
		{ offset: '70%' }
	);


	//after user choosen
	var flag=true;
	$('.timu_list li').click(function() {
		if( $(this).siblings('li').hasClass('on')!=true ){
			$(this).addClass('on');
			
			var idName=$(this).parent().parent().attr('id');
			console.log(idName);

			if(flag==true){
				$('#'+idName).find('.dun').addClass('animated pulse');
				$('#'+idName).find('.jian1').addClass('animated move1');
				$('#'+idName).find('.jian2').addClass('animated move2');
				
				setTimeout( function(){
					$('#'+idName).find('.timu_mask').fadeIn(300, function(){
						$('#'+idName).find('.counting_down').fadeIn(400);

						timer(115-2, $('#'+idName).find('.counting') );
						


					})
				},2000);

				setTimeout( function(){
					$('#sound-jian').get(0).play();
				},600);
			}
			flag=false;
		}
	});



	

function timer(intDiff,oHandle) {
	t2 = window.setInterval(countDown2, 1000);
	countDown2();

	function countDown2() {
		var day = 0,
			hour = 0,
			minute = 0,
			second = 0; //时间默认值
		if (intDiff > 0) {
			day = Math.floor(intDiff / (60 * 60 * 24));
			hour = Math.floor(intDiff / (60 * 60)) - (day * 24);
			minute = Math.floor(intDiff / 60) - (day * 24 * 60) - (hour * 60);
			second = Math.floor(intDiff) - (day * 24 * 60 * 60) - (hour * 60 * 60) - (minute * 60);
		}
		if (minute <= 9) minute = '0' + minute;
		if (second <= 9) second = '0' + second;


		oHandle.find('span').eq(0).html(minute);
		oHandle.find('span').eq(1).html(second);
		// $('.single_question').eq(questionNum-1).find('.c_sec').html(second);

		// if(minute==0 && second<15 && $('.single_question').eq(questionNum).hasClass('answered')==false){
		// 	$('.single_question').eq(questionNum).find('.counting_circle').show().find('strong').show().prev().hide();
		// 	$('.single_question').eq(questionNum).find('.c_min').hide().next().hide();
		// 	$('.single_question').eq(questionNum).find('.c_sec').html(second).css({color:'red'});
		// }

		intDiff--;

		if (intDiff < 0) {
			clearInterval(t2);
		}
	}
}






